mapper.select:
select users.USER_ID, users.LOGIN_NAME, users.USER_FULL_NAME, users.PASSWORD, users.EMAIL, users.MOBILE_PHONE, users.STATUS, decode(users.STATUS,'1','启用','禁用') STATUS_NAME, users.CREATE_TIME, users.CREATED_BY, users.UPDATED_BY, users.UPDATE_TIME, users.FAIL_COUNT, users.LAST_LOGIN_TIME, users.SECURITY_LEVEL, decode(users.SECURITY_LEVEL,0,'公开',1,'内部',2,'秘密',3,'机密',4,'绝密') SECURITY_LEVEL_NAME from TBL_USER users where users.DELETED = 0
AND users.LOGIN_NAME <> #{searchParams.admin}
AND users.${key} like '%'||#{value}||'%'
order by users.${searchParams.sortField} ${searchParams.sortType}

select users.USER_ID, users.LOGIN_NAME, users.USER_FULL_NAME, users.STATUS, decode(users.STATUS,'1','启用','禁用') STATUS_NAME, users.SECURITY_LEVEL, decode(users.SECURITY_LEVEL,0,'公开',1,'内部',2,'秘密',3,'机密',4,'绝密') SECURITY_LEVEL_NAME, userRole.ROLE_ID, roles.ROLE_NAME ROLE_NAME, permission.permission_name from TBL_USER users inner join TBL_USER_ROLE userRole on
users.LOGIN_NAME <> #{admin} AND
users.DELETED = 0 and userRole.USER_ID = users.USER_ID inner join TBL_ROLE roles on roles.oid = userRole.ROLE_ID inner join ROLE_PERMISSION rolePermission on rolePermission.ROLE_ID = roles.oid inner join TBL_PERMISSION permission on permission.oid = rolePermission.PERMISSION_ID and permission.permission_code = #{type}
